        private static int CalculateFloor(string input)
        {
            int floor = 0;
            int index = 1;

            foreach (Char c in input)
            {
                if (c.ToString() == "(")
                {
                    floor += 1;
                }
                if (c.ToString() == ")")
                {
                    floor -= 1;
                }

                if (floor == -1)
                {
                    Console.Write($"First time in the basement is step: ");

                    CustomConsoleColour.SetAnswerColour();
                    Console.Write(index);
                    Console.ResetColor();

                    Console.WriteLine();
                    break;
                }

                index++;
            }

            return floor;
        }
